import React, { useState } from 'react';

function AdminBlogs() {
    const [blogs, setBlogs] = useState([]);
    const [newBlog, setNewBlog] = useState('');

    const handleBlogSubmit = (e) => {
        e.preventDefault();

        if (newBlog.trim() === '') {
            alert('Please enter a blog post.');
            return;
        }

        const newBlogObj = { text: newBlog };
        setBlogs([...blogs, newBlogObj]);

        // Clear the blog input
        setNewBlog('');
    };

    return (
        <div>
            <h2>Admin Blogs</h2>

            <form onSubmit={handleBlogSubmit}>
                <div className="mb-3">
                    <label htmlFor="blog" className="form-label">Write a blog:</label>
                    <textarea
                        id="blog"
                        className="form-control"
                        value={newBlog}
                        onChange={(e) => setNewBlog(e.target.value)}
                    />
                </div>
                <button type="submit" className="btn btn-primary">Post Blog</button>
            </form>

            {blogs.length > 0 && (
                <div>
                    <h3>Recent Blogs:</h3>
                    {blogs.map((blog, index) => (
                        <div key={index} className="blog">
                            <p>{blog.text}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default AdminBlogs;
