import React, { useState } from 'react';

function Blogs() {
    const [blogs, setBlogs] = useState([]);
    const [newBlog, setNewBlog] = useState('');
    const [newComment, setNewComment] = useState('');

    const handleBlogSubmit = (e) => {
        e.preventDefault();

        if (newBlog.trim() === '') {
            alert('Please enter a blog post.');
            return;
        }

        const newBlogObj = { text: newBlog, comments: [] };
        setBlogs([...blogs, newBlogObj]);

        // Clear the blog input
        setNewBlog('');
    };

    const handleCommentSubmit = (blogIndex) => {
        if (newComment.trim() === '') {
            alert('Please enter a comment.');
            return;
        }

        const updatedBlogs = [...blogs];
        updatedBlogs[blogIndex].comments.push(newComment);
        setBlogs(updatedBlogs);

        // Clear the comment input
        setNewComment('');
    };

    return (
        <div>
            <h2>Blogs</h2>

            <form onSubmit={handleBlogSubmit}>
                <div className="mb-3">
                    <label htmlFor="blog" className="form-label">Write a blog:</label>
                    <textarea
                        id="blog"
                        className="form-control"
                        value={newBlog}
                        onChange={(e) => setNewBlog(e.target.value)}
                    />
                </div>
                <button type="submit" className="btn btn-primary">Post Blog</button>
            </form>

            {blogs.length > 0 && (
                <div>
                    <h3>Recent Blogs:</h3>
                    {blogs.map((blog, index) => (
                        <div key={index} className="blog">
                            <p>{blog.text}</p>

                            <div>
                                <h4>Comments:</h4>
                                <ul>
                                    {blog.comments.map((comment, commentIndex) => (
                                        <li key={commentIndex}>{comment}</li>
                                    ))}
                                </ul>
                                <div>
                                    <input
                                        type="text"
                                        placeholder="Add a comment..."
                                        value={newComment}
                                        onChange={(e) => setNewComment(e.target.value)}
                                    />
                                    <button
                                        className="btn btn-secondary"
                                        onClick={() => handleCommentSubmit(index)}
                                    >
                                        Add Comment
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default Blogs;
