// import react from 'react';

export default function Blogs() {
    return (
        <>
            <h2 style={{ marginLeft: "5rem", marginTop: "2rem" }}>Coding Articles</h2>
            <div className="card " style={{ width: '60rem', marginLeft: "5rem" }}>
                <div className="card-body">
                    <h5 className="card-title">Why a site made with React.js is called single-page web-application?</h5>
                    <p className="card-text">A site made with React.js is often referred to as a "single-page web application" (SPA) because it operates as a single, dynamic web page that loads and updates content without requiring full page reloads. Unlike traditional multi-page websites, where each page is a separate HTML document, SPAs load a single HTML page initially and then use JavaScript to dynamically update the content as needed.</p>
                    <a href="/" className="btn btn-light" style={{ color: "#6e007e" }}>Read more</a>
                </div>
            </div>
        </>
    );
}
